mapper.insert:
BEGIN TRAN;
	insert into clan(name, description, leaderId, locked, password, created_at, clanmark, clanmarkdeletehash, clanpoint)
	values(#{name}, #{description}, #{leaderId}, #{locked}, #{password}, now(), #{clanmark}, #{clanmarkdeletehash}, #{clanpoint})
	from clan;
	insert into user(clanid)
	values(2)
	from user;
COMMIT TRAN;

mapper.update:
update user
set clanid = #{clan_id}
where id = #{user_id}

update user
set clanid = 0
where id=#{user_id}
